    super(props);
    this.state = {
      data : null
    }
  }

  componentWillMount(){
    console.log(getProductData());
    if(this.state.data === null){
      getProductData().then( function(res) {
        this.setState({
          data : res
        });
    });
    }
  }

  printData = () => {
    if(this.state.data !== null){
      return this.state.data.map((value, key) => 
        (
          <Product 
                key = {key}
                product_name = {value.product_name}
                product_price = {value.product_price}
                image = {value.image}>
                </Product>
        )
      )
    }
  }

  render() {
    console.log(this.state.data);
    return (
      <div>
        <HeadTitle></HeadTitle>
        <div className="container">
            <div className="row">
              {this.printData()}
            </div>
        </div>
      </div>
    );
  }
}

export default App;
